import React, { useState, useEffect, useRef } from 'react';
import { 
  ChevronDown, Github, Linkedin, Mail, ExternalLink, Code, Database, 
  Cloud, MessageSquare, ArrowRight, Menu, X, 
  Star, Sparkles, Rocket, Target, Globe, Cpu, Heart,
  Download, Calendar, MapPin, Terminal, Activity, User, BriefcaseBusiness
} from 'lucide-react';

const Portfolio = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [activeSection, setActiveSection] = useState('home');
  const [animatedCounters, setAnimatedCounters] = useState(false);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [isTyping, setIsTyping] = useState(false);
  const heroRef = useRef(null);
  const projectRef = useRef(null);

  // Mouse tracking for interactive effects
  useEffect(() => {
    const handleMouseMove = (e) => {
      setMousePosition({ x: e.clientX, y: e.clientY });
    };
    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  // Scroll spy effect
  useEffect(() => {
    const handleScroll = () => {
      const sections = ['home', 'about', 'projects', 'skills', 'contact'];
      const scrollPosition = window.scrollY + 100;

      sections.forEach(section => {
        const element = document.getElementById(section);
        if (element) {
          const offsetTop = element.offsetTop;
          const height = element.offsetHeight;

          if (scrollPosition >= offsetTop && scrollPosition < offsetTop + height) {
            setActiveSection(section);
          }
        }
      });
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Counter animation trigger
  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach(entry => {
          if (entry.isIntersecting && !animatedCounters) {
            setAnimatedCounters(true);
          }
        });
      },
      { threshold: 0.5 }
    );

    if (projectRef.current) {
      observer.observe(projectRef.current);
    }

    return () => observer.disconnect();
  }, [animatedCounters]);

  // Smooth scroll function
  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
    setIsMenuOpen(false);
  };

  // Animated counter component
  const AnimatedCounter = ({ end, suffix = '', duration = 2000, prefix = '' }) => {
    const [count, setCount] = useState(0);

    useEffect(() => {
      if (!animatedCounters) return;

      let startTime;
      const animate = (timestamp) => {
        if (!startTime) startTime = timestamp;
        const progress = Math.min((timestamp - startTime) / duration, 1);

        if (suffix === '%') {
          setCount(Math.floor(progress * (end * 10)) / 10);
        } else {
          setCount(Math.floor(progress * end));
        }

        if (progress < 1) {
          requestAnimationFrame(animate);
        }
      };

      requestAnimationFrame(animate);
    }, [animatedCounters, end, duration, suffix]);

    return <span>{prefix}{suffix === 'ms' ? `< ${count}` : count}{suffix}</span>;
  };

  // Floating particles with mouse interaction
  const InteractiveParticles = () => {
    const particles = Array.from({ length: 50 }, (_, i) => {
      const distance = Math.sqrt(
        Math.pow(mousePosition.x - (window.innerWidth * Math.random()), 2) +
        Math.pow(mousePosition.y - (window.innerHeight * Math.random()), 2)
      );
      const scale = Math.max(0.5, 1 - distance / 500);

      return (
        <div
          key={i}
          className="absolute w-1 h-1 bg-blue-400/30 rounded-full animate-pulse transition-transform duration-500"
          style={{
            left: `${Math.random() * 100}%`,
            top: `${Math.random() * 100}%`,
            animationDelay: `${Math.random() * 5}s`,
            animationDuration: `${3 + Math.random() * 4}s`,
            transform: `scale(${scale})`
          }}
        />
      );
    });

    return <div className="absolute inset-0 overflow-hidden pointer-events-none">{particles}</div>;
  };

  // Enhanced Architecture Component with animations
  const ArchitectureDiagram = () => {
    const [hoveredService, setHoveredService] = useState(null);
    const [pulseAnimation, setPulseAnimation] = useState(false);

    useEffect(() => {
      const interval = setInterval(() => {
        setPulseAnimation(prev => !prev);
      }, 2000);
      return () => clearInterval(interval);
    }, []);

    const services = [
      { id: 'gateway', x: 250, y: 50, width: 100, height: 60, label: 'API Gateway', port: '8080', color: '#3498db', icon: 'üö™' },
      { id: 'eureka', x: 400, y: 50, width: 100, height: 60, label: 'Eureka', port: '8761', color: '#9b59b6', icon: 'üîç' },
      { id: 'auth', x: 50, y: 150, width: 90, height: 55, label: 'Auth Service', port: '8081', color: '#27ae60', icon: 'üîê' },
      { id: 'profile', x: 170, y: 150, width: 90, height: 55, label: 'Profile Service', port: '8082', color: '#27ae60', icon: 'üë§' },
      { id: 'lending', x: 290, y: 150, width: 90, height: 55, label: 'Lending Engine', port: '8083', color: '#27ae60', icon: 'üí∞' },
      { id: 'rabbitmq', x: 410, y: 150, width: 90, height: 55, label: 'RabbitMQ', port: '5672', color: '#f39c12', icon: 'üê∞' },
      { id: 'database', x: 220, y: 250, width: 100, height: 60, label: 'MySQL', port: '3307', color: '#e74c3c', icon: 'üóÑÔ∏è' }
    ];

    return (
      <div className="relative bg-gradient-to-br from-gray-900/50 to-blue-900/30 rounded-2xl p-8 border border-blue-500/20 backdrop-blur-sm">
        <div className="flex items-center justify-center gap-2 mb-6">
          <Cpu className="w-6 h-6 text-blue-400" />
          <h4 className="text-xl font-bold text-white">
            Interactive System Architecture
          </h4>
          <div className="flex gap-1">
            <div className="w-2 h-2 bg-green-400 rounded-full animate-pulse" />
            <div className="w-2 h-2 bg-yellow-400 rounded-full animate-pulse" style={{ animationDelay: '0.5s' }} />
            <div className="w-2 h-2 bg-red-400 rounded-full animate-pulse" style={{ animationDelay: '1s' }} />
          </div>
        </div>

        <svg
          viewBox="0 0 550 350"
          className="w-full h-auto cursor-pointer"
          style={{ maxHeight: '450px' }}
        >
          {/* Animated connection lines */}
          <defs>
            <linearGradient id="lineGradient" x1="0%" y1="0%" x2="100%" y2="0%">
              <stop offset="0%" stopColor="#3498db" stopOpacity="0.8" />
              <stop offset="50%" stopColor="#9b59b6" stopOpacity="0.6" />
              <stop offset="100%" stopColor="#f39c12" stopOpacity="0.8" />
            </linearGradient>
            <filter id="glow">
              <feGaussianBlur stdDeviation="3" result="coloredBlur"/>
              <feMerge> 
                <feMergeNode in="coloredBlur"/>
                <feMergeNode in="SourceGraphic"/>
              </feMerge>
            </filter>
          </defs>

          {/* Connection Lines with animation */}
          <g stroke="url(#lineGradient)" strokeWidth="2" fill="none" filter="url(#glow)">
            <line x1="300" y1="110" x2="95" y2="150" strokeDasharray="5,5" opacity={pulseAnimation ? "1" : "0.6"} />
            <line x1="300" y1="110" x2="215" y2="150" strokeDasharray="5,5" opacity={pulseAnimation ? "0.8" : "1"} />
            <line x1="300" y1="110" x2="335" y2="150" strokeDasharray="5,5" opacity={pulseAnimation ? "1" : "0.7"} />
            <line x1="350" y1="80" x2="400" y2="80" stroke="#9b59b6" strokeDasharray="3,3" />
            <line x1="95" y1="205" x2="250" y2="250" stroke="#e74c3c" strokeWidth="3" />
            <line x1="215" y1="205" x2="270" y2="250" stroke="#e74c3c" strokeWidth="3" />
            <line x1="335" y1="205" x2="290" y2="250" stroke="#e74c3c" strokeWidth="3" />
            <line x1="380" y1="177" x2="410" y2="177" stroke="#f39c12" strokeDasharray="2,2" />
          </g>

          {/* Service Nodes with enhanced styling */}
          {services.map(service => (
            <g
              key={service.id}
              className={`transition-all duration-500 cursor-pointer ${
                hoveredService === service.id ? 'scale-110' : ''
              }`}
              onMouseEnter={() => setHoveredService(service.id)}
              onMouseLeave={() => setHoveredService(null)}
              style={{ transformOrigin: `${service.x + service.width/2}px ${service.y + service.height/2}px` }}
            >
              <defs>
                <linearGradient id={`gradient-${service.id}`} x1="0%" y1="0%" x2="100%" y2="100%">
                  <stop offset="0%" stopColor={service.color} />
                  <stop offset="100%" stopColor={service.color + "dd"} />
                </linearGradient>
              </defs>
              <rect
                x={service.x}
                y={service.y}
                width={service.width}
                height={service.height}
                rx="12"
                fill={`url(#gradient-${service.id})`}
                stroke={hoveredService === service.id ? '#ffffff' : 'transparent'}
                strokeWidth="2"
                className="drop-shadow-xl"
                filter={hoveredService === service.id ? "url(#glow)" : "none"}
              />
              <text
                x={service.x + service.width/2}
                y={service.y + service.height/2 - 8}
                textAnchor="middle"
                fill="white"
                fontSize="10"
                fontWeight="bold"
              >
                {service.icon} {service.label}
              </text>
              <text
                x={service.x + service.width/2}
                y={service.y + service.height/2 + 8}
                textAnchor="middle"
                fill="rgba(255,255,255,0.9)"
                fontSize="8"
              >
                :{service.port}
              </text>
            </g>
          ))}
        </svg>

        {hoveredService && (
          <div className="mt-6 p-4 bg-gradient-to-r from-blue-900/30 to-purple-900/30 rounded-xl border border-blue-400/30 backdrop-blur-sm">
            <div className="flex items-center gap-3">
              <div className="w-3 h-3 bg-green-400 rounded-full animate-pulse" />
              <p className="text-sm text-blue-100">
                <strong className="text-blue-300">{services.find(s => s.id === hoveredService)?.label}:</strong> 
                {hoveredService === 'gateway' && ' Intelligent request routing with load balancing, rate limiting, and security filters'}
                {hoveredService === 'eureka' && ' Dynamic service discovery with health monitoring and auto-scaling capabilities'}
                {hoveredService === 'auth' && ' JWT-based authentication with role-based access control and session management'}
                {hoveredService === 'profile' && ' Comprehensive user profile management with validation and audit trails'}
                {hoveredService === 'lending' && ' AI-powered loan processing with real-time credit scoring and risk assessment'}
                {hoveredService === 'rabbitmq' && ' High-performance message broker with dead letter queues and retry mechanisms'}
                {hoveredService === 'database' && ' ACID-compliant data storage with connection pooling and query optimization'}
              </p>
            </div>
          </div>
        )}
      </div>
    );
  };

  // Testimonial/Achievement component
  const AchievementCard = ({ icon, title, description, metric }) => (
    <div className="group relative overflow-hidden bg-gradient-to-br from-gray-800/50 to-gray-900/50 rounded-2xl p-6 border border-gray-700/50 hover:border-blue-500/50 transition-all duration-500 hover:transform hover:scale-105">
      <div className="absolute inset-0 bg-gradient-to-br from-blue-500/5 to-purple-500/5 opacity-0 group-hover:opacity-100 transition-opacity duration-500" />
      <div className="relative z-10">
        <div className="flex items-center gap-4 mb-4">
          <div className="p-3 bg-gradient-to-br from-blue-500/20 to-purple-500/20 rounded-xl text-blue-400 group-hover:scale-110 transition-transform duration-300">
            {icon}
          </div>
          <div>
            <h3 className="font-bold text-white">{title}</h3>
            <p className="text-blue-400 font-semibold">{metric}</p>
          </div>
        </div>
        <p className="text-gray-300 text-sm leading-relaxed">{description}</p>
      </div>
    </div>
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-blue-900 to-purple-900 text-white relative overflow-hidden">
      <InteractiveParticles />

      {/* Navigation */}
      <nav className="fixed top-0 w-full bg-gray-900/80 backdrop-blur-2xl z-50 border-b border-white/10">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-4">
            <div className="flex items-center gap-3">
              <div className="w-10 h-10 bg-gradient-to-br from-blue-500 to-purple-600 rounded-xl flex items-center justify-center">
                <Terminal className="w-6 h-6 text-white" />
              </div>
              <div className="text-xl font-bold bg-gradient-to-r from-blue-400 to-purple-400 bg-clip-text text-transparent">
                Ganeev.dev
              </div>
            </div>

            {/* Desktop Menu */}
            <div className="hidden md:flex items-center space-x-8">
              {[
                { id: 'home', label: 'Home', icon: <Globe className="w-4 h-4" /> },
                { id: 'experience', label: 'Experience', icon: <BriefcaseBusiness className="w-4 h-4" /> },
                { id: 'skills', label: 'Skills', icon: <Code className="w-4 h-4" /> },
                { id: 'projects', label: 'Projects', icon: <Rocket className="w-4 h-4" /> },
                { id: 'contact', label: 'Contact', icon: <Mail className="w-4 h-4" /> }
              ].map(item => (
                <button
                  key={item.id}
                  onClick={() => scrollToSection(item.id)}
                  className={`flex items-center gap-2 px-4 py-2 rounded-full transition-all duration-300 ${
                    activeSection === item.id 
                      ? 'bg-blue-500/20 text-blue-400 border border-blue-500/30' 
                      : 'hover:text-blue-400 hover:bg-white/5'
                  }`}
                >
                  {item.icon}
                  {item.label}
                </button>
              ))}
            </div>

            {/* Mobile Menu Button */}
            <button
              className="md:hidden p-2 rounded-lg bg-white/10"
              onClick={() => setIsMenuOpen(!isMenuOpen)}
            >
              {isMenuOpen ? <X /> : <Menu />}
            </button>
          </div>
        </div>

        {/* Mobile Menu */}
        {isMenuOpen && (
          <div className="md:hidden bg-gray-900/95 backdrop-blur-xl border-t border-white/10">
            <div className="px-4 py-6 space-y-4">
              {['home', 'about', 'projects', 'skills', 'contact'].map(section => (
                <button
                  key={section}
                  onClick={() => scrollToSection(section)}
                  className="block w-full text-left capitalize hover:text-blue-400 transition-colors py-3 px-4 rounded-lg hover:bg-white/5"
                >
                  {section}
                </button>
              ))}
            </div>
          </div>
        )}
      </nav>

      {/* Hero Section */}
      <section id="home" ref={heroRef} className="min-h-screen flex items-center justify-center px-4 pt-20">
        <div className="max-w-6xl mx-auto text-center">
          <div className="relative">
            {/* Background glow effect */}
            <div className="absolute inset-0 bg-gradient-to-r from-blue-500/20 to-purple-500/20 blur-3xl rounded-full" />

            <div className="relative bg-white/5 backdrop-blur-2xl rounded-3xl p-8 md:p-16 shadow-2xl border border-white/10">
              {/* <div className="flex justify-center mb-8"> */}
                {/* <div className="relative"> */}
                  {/* <div className="w-32 h-32 bg-gradient-to-br from-blue-500 to-purple-600 rounded-full flex items-center justify-center text-4xl animate-pulse">
                    üë®‚Äçüíª
                  </div>
                  <div className="absolute -top-2 -right-2 w-8 h-8 bg-green-500 rounded-full flex items-center justify-center">
                    <div className="w-3 h-3 bg-white rounded-full animate-ping" />
                  </div> */}
                {/* </div> */}
              {/* </div> */}

              <h1 className="text-4xl md:text-4xl font-bold mb-6">
                <span className="bg-gradient-to-r from-blue-400 via-purple-400 to-pink-400 bg-clip-text text-transparent">
                  GANEEV KAUR CHHABRA
                </span>
              </h1>

              <div className="flex items-center justify-center gap-4 mb-6">
                <div className="flex items-center gap-2 px-4 py-2 bg-blue-500/20 rounded-full border border-blue-500/30">
                  <Star className="w-4 h-4 text-yellow-400" />
                  <span className="text-xl text-blue-300 font-semibold">Bachelor's of Technology in Computer Science & Engineering</span>
                </div>
                {/* <div className="flex items-center gap-2 px-4 py-2 bg-purple-500/20 rounded-full border border-purple-500/30">
                  <Trophy className="w-4 h-4 text-purple-400" />
                  <span className="text-xl text-purple-300 font-semibold">Microservices Architect</span>
                </div> */}
              </div>

              <p className="text-lg md:text-xl text-gray-300 mb-4 max-w-3xl mx-auto leading-relaxed">
              Passionate about building reliable and scalable software solutions with a strong focus on real-world impact. Experienced in developing modern applications using  
               <span className="text-blue-400 font-semibold"> Java and Spring Boot</span>, and interested in 
                <span className="text-blue-400 font-semibold"> cloud-based systems & microservices</span>
                . I enjoy solving complex problems and creating software that adds lasting value.
              </p>

              <div className="flex items-center justify-center gap-6 mb-8 text-gray-400">
                <div className="flex items-center gap-2">
                  <MapPin className="w-4 h-4" />
                  <span>New Delhi, India</span>
                </div>
                <div className="flex items-center gap-2">
                  <Calendar className="w-4 h-4" />
                  <span>Available for opportunities</span>
                </div>
                {/* <div className="flex items-center gap-2">
                  <Coffee className="w-4 h-4" />
                  <span>5+ years experience</span>
                </div> */}
              </div>

              <div className="flex flex-col sm:flex-row gap-4 justify-center mb-8">
                <button
                  onClick={() => scrollToSection('projects')}
                  className="group bg-gradient-to-r from-blue-500 to-purple-600 hover:from-blue-600 hover:to-purple-700 px-8 py-4 rounded-full font-semibold transition-all duration-300 hover:scale-105 hover:shadow-2xl flex items-center justify-center gap-3"
                >
                  <Rocket className="w-5 h-5 group-hover:animate-bounce" />
                  Explore My Work
                  <Sparkles className="w-5 h-5" />
                </button>
                <a
                  href="https://github.com/ganeevkc"
                  target="_blank"
                  rel="noopener noreferrer"
                  className="group border-2 border-blue-400 hover:bg-blue-400 hover:text-gray-900 px-8 py-4 rounded-full font-semibold transition-all duration-300 hover:scale-105 flex items-center justify-center gap-3"
                >
                  <Github className="w-5 h-5 group-hover:rotate-12" />
                  GitHub Profile
                  <ExternalLink className="w-4 h-4" />
                </a>
                <button
                  onClick={() => {
                    const link = document.createElement("a");
                    link.href="/ganeev_resume.pdf";
                    link.download="ganeev_resume.pdf";
                    document.body.appendChild(link);
                    link.click();
                    document.body.removeChild(link);
                  }}
                  className="group border-2 border-green-400 hover:bg-green-400 hover:text-gray-900 px-8 py-4 rounded-full font-semibold transition-all duration-300 hover:scale-105 flex items-center justify-center gap-3"
                >
                  <Download className="w-5 h-5 group-hover:animate-bounce" />
                  Download Resume
                </button>
              </div>

              <div className="flex flex-wrap justify-center gap-6">
                <a href="mailto:your.email@example.com" className="group flex items-center gap-3 text-gray-300 hover:text-blue-400 transition-all duration-300 hover:scale-105">
                  <div className="p-2 bg-blue-500/20 rounded-lg group-hover:bg-blue-500/30">
                    <Mail className="w-5 h-5" />
                  </div>
                  <span>ganeevkc23@gmail.com</span>
                </a>
                <a href="https://linkedin.com/in/ganeevkc" target="_blank" rel="noopener noreferrer" className="group flex items-center gap-3 text-gray-300 hover:text-blue-400 transition-all duration-300 hover:scale-105">
                  <div className="p-2 bg-blue-500/20 rounded-lg group-hover:bg-blue-500/30">
                    <Linkedin className="w-5 h-5" />
                  </div>
                  <span>LinkedIn</span>
                </a>
                {/* <a href="tel:+1234567890" className="group flex items-center gap-3 text-gray-300 hover:text-blue-400 transition-all duration-300 hover:scale-105">
                  <div className="p-2 bg-blue-500/20 rounded-lg group-hover:bg-blue-500/30">
                    <Phone className="w-5 h-5" />
                  </div>
                  <span>+91 98765 43210</span>
                </a> */}
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* About Section */}
      {/* <section id="about" className="py-20 px-4">
        <div className="max-w-6xl mx-auto">
          <div className="text-center mb-16">
            <h2 className="text-4xl md:text-5xl font-bold mb-6 bg-gradient-to-r from-blue-400 to-purple-400 bg-clip-text text-transparent">
              About Me
            </h2>
            <p className="text-xl text-gray-300 max-w-3xl mx-auto">
              A passionate developer with a love for creating scalable solutions and beautiful user experiences
            </p>
          </div>

          <div className="grid lg:grid-cols-3 gap-8">
            <AchievementCard
              icon={<Trophy className="w-8 h-8" />}
              title="Enterprise Solutions"
              metric="50+ Projects Delivered"
              description="Successfully architected and delivered enterprise-grade microservices solutions for fintech, healthcare, and e-commerce domains."
            />
            <AchievementCard
              icon={<Users className="w-8 h-8" />}
              title="Team Leadership"
              metric="10+ Developers Mentored"
              description="Led cross-functional teams and mentored junior developers in best practices, code reviews, and architectural decisions."
            />
            <AchievementCard
              icon={<Shield className="w-8 h-8" />}
              title="System Reliability"
              metric="99.9% Uptime"
              description="Designed and maintained highly available systems with robust monitoring, alerting, and disaster recovery capabilities."
            />
          </div>
        </div>
      </section> */}
      {/* Skills Section */}
      <section id="skills" className="py-20 px-4">
        <div className="max-w-[85%] mx-auto">
          <div className="text-center mb-16">
            <div className="flex items-center justify-center gap-3 mb-6">
              <Code className="w-8 h-8 text-blue-400" />
              <h2 className="text-4xl md:text-5xl font-bold bg-gradient-to-r from-blue-400 to-purple-400 bg-clip-text text-transparent">
                Technical Expertise
              </h2>
              <Terminal className="w-8 h-8 text-purple-400" />
            </div>
            <p className="text-xl text-gray-300">
              Cutting-edge technologies and frameworks I work with
            </p>
          </div>

          <div className="grid md:grid-cols-2 lg:grid-cols-5 gap-5">
            {[
              {
                icon: <Code className="w-8 h-8" />,
                title: 'Frontend Development',
                gradient: 'from-pink-600 to-red-500',
                skills: [
                  { name: 'HTML, CSS & JavaScript', level: 95 },
                  { name: 'ReactJS', level: 89 },
                  { name: 'TailwindCSS', level: 87 },
                  { name: 'Bootstrap', level: 85 },
                  { name: 'Vite', level: 85 }
                ]
              },
              {
                icon: <Code className="w-8 h-8" />,
                title: 'Backend Development',
                gradient: 'from-blue-500 to-cyan-500',
                skills: [
                  { name: 'Java 11+ & Spring Framework', level: 95 },
                  { name: 'Spring Boot & Spring Cloud', level: 92 },
                  { name: 'Spring Security & JWT', level: 90 },
                  { name: 'RESTful API Design', level: 90 },
                  { name: 'Microservices Architecture', level: 85 }
                ]
              },
              {
                icon: <Database className="w-8 h-8" />,
                title: 'Database Technologies',
                gradient: 'from-green-500 to-emerald-500',
                skills: [
                  { name: 'MySQL', level: 93 },
                  { name: 'MongoDB', level: 90 },
                  { name: 'JPA/Hibernate ORM', level: 85 },
                  { name: 'Database Design & Optimization', level: 82 },
                  { name: 'Transaction Management', level: 80 },
